// Parse email data and identify newsletter
const emailData = $input.first().json;

// Extract key information
const fromEmail = emailData.from;
const subject = emailData.subject;
const htmlContent = emailData.html || '';
const textContent = emailData.text || '';
const receivedAt = emailData.received_at || new Date().toISOString();

// Extract user ID from recipient email
const recipientEmail = emailData.to;
const userId = recipientEmail.split('@')[0].replace('user', '');

// Extract sender domain
const senderDomain = fromEmail.split('@')[1];

// Identify known newsletters
const knownNewsletters = {
  'morningbrew.com': { name: 'Morning Brew', frequency: 'daily' },
  'techcrunch.com': { name: 'TechCrunch', frequency: 'daily' },
  'thehustle.co': { name: 'The Hustle', frequency: 'daily' },
  'ben-evans.com': { name: 'Benedict Evans', frequency: 'weekly' },
  'stratechery.com': { name: 'Stratechery', frequency: 'weekly' }
};

const newsletterInfo = knownNewsletters[senderDomain] || {
  name: 'Unknown Newsletter',
  frequency: 'unknown'
};

// Extract articles from content (simple implementation)
const articles = [];

// For HTML content, try to extract article sections
if (htmlContent) {
  // This is a basic extraction - you might want to enhance this
  const articlePatterns = [
    /<h[1-3][^>]*>(.*?)<\/h[1-3]>/gi,
    /<div[^>]*class[^>]*article[^>]*>(.*?)<\/div>/gi,
    /<p[^>]*>(.*?)<\/p>/gi
  ];

  let articleContent = htmlContent;
  let matches;

  // Extract titles/headers
  const titleRegex = /<h[1-3][^>]*>(.*?)<\/h[1-3]>/gi;
  let articleIndex = 0;

  while ((matches = titleRegex.exec(htmlContent)) !== null && articleIndex < 5) {
    const title = matches[1].replace(/<[^>]*>/g, '').trim();
    if (title.length > 10) { // Only consider meaningful titles
      articles.push({
        title: title,
        content: '', // Will be filled by article extraction
        url: '', // Will be extracted if available
        excerpt: ''
      });
      articleIndex++;
    }
  }
}

// If no articles found in HTML, use subject as single article
if (articles.length === 0) {
  articles.push({
    title: subject,
    content: textContent || htmlContent.replace(/<[^>]*>/g, ''),
    url: '',
    excerpt: textContent.substring(0, 200)
  });
}

return {
  userId: userId,
  fromEmail: fromEmail,
  subject: subject,
  senderDomain: senderDomain,
  newsletterName: newsletterInfo.name,
  frequency: newsletterInfo.frequency,
  receivedAt: receivedAt,
  articles: articles,
  rawHtml: htmlContent,
  rawText: textContent
};

// Combine all AI results with original data
const originalData = $input.first().json;
const summaryResponse = $input.all()[1]?.json;
const categoryResponse = $input.all()[2]?.json;
const interestResponse = $input.all()[3]?.json;

// Extract AI responses
const aiSummary = summaryResponse?.content?.[0]?.text || null;
const aiCategory = categoryResponse?.content?.[0]?.text?.trim() || 'Other';
const aiInterestScore = parseFloat(interestResponse?.content?.[0]?.text?.trim() || '0.5');

// Prepare articles with AI enhancements
const enhancedArticles = originalData.articles.map(article => ({
  ...article,
  aiSummary: aiSummary,
  aiCategory: aiCategory,
  aiInterestScore: Math.max(0, Math.min(1, aiInterestScore)) // Ensure score is between 0 and 1
}));

return {
  ...originalData,
  articles: enhancedArticles,
  processingStatus: 'completed',
  aiProcessed: true,
  processedAt: new Date().toISOString()
};

// Log successful processing
const response = $input.first().json;

console.log('âœ… Email processing completed successfully:', {
  userId: response.userId,
  newsletter: response.newsletterName,
  articlesProcessed: response.articles?.length || 0,
  aiEnhanced: response.aiProcessed,
  timestamp: new Date().toISOString()
});

return {
  status: 'completed',
  message: 'Email processed and articles saved with AI enhancement',
  articleCount: response.articles?.length || 0,
  aiProcessed: response.aiProcessed
};
